const ChatContext = createContext();

export const chatProvider = ({children}) => {
    const [messages, setMessages] = useState([]);

    const values = {
        messages,
        setMessages,
    };

    return <ChatContext.Provider>{children}</ChatContext.Provider>
}

export default ChatContext;
